// SPDX-License-Identifier: MIT
pragma solidity >=0.4.22 <0.9.0;

contract StudentContract {
    // Struct to represent a student
    struct Student {
        uint256 id;
        string name;
        uint256 age;
        uint256 yearOfGraduate;
        string pathToTheDegree;
    }

    // Mapping to store students using their ID as the key
    mapping(uint256 => Student) public students;

    // Counter to keep track of the number of students
    uint256 public studentsCount;

    // Event emitted when a new student is added
    event StudentAdded(uint256 id, string name, uint256 age, uint256 yearOfGraduate, string pathToTheDegree);

    // Function to add a new student
    function addStudent(string memory _name, uint256 _age, uint256 _yearOfGraduate, string memory _pathToTheDegree) public {
        studentsCount++;
        students[studentsCount] = Student(studentsCount, _name, _age, _yearOfGraduate, _pathToTheDegree);
        emit StudentAdded(studentsCount, _name, _age, _yearOfGraduate, _pathToTheDegree);
    }

    // Function to get student details by ID
    function getStudent(uint256 _id) public view returns (uint256, string memory, uint256, uint256, string memory) {
        require(_id > 0 && _id <= studentsCount, "Invalid student ID");
        Student memory student = students[_id];
        return (student.id, student.name, student.age, student.yearOfGraduate, student.pathToTheDegree);
    }
}
