// SPDX-License-Identifier: MIT
pragma solidity >=0.4.22 <0.9.0;

import "./StudentContract.sol"; // Import the StudentContract

contract DegreeContract {
    // Enum to represent diploma mentions
    enum DiplomaMention { Pass, Fair, Good, VeryGood, Excellent }

    // Struct to represent a degree
    struct Degree {
        uint256 degreeId;
        uint256 matricule; // Matricule field added
        uint256 studentId; // Associated student ID
        string degreeName;
        uint256 completionYear;
        uint256 averageObtention;
        DiplomaMention diplomaMention;
    }

    // Mapping to store degrees using their ID as the key
    mapping(uint256 => Degree) public degrees;

    // Counter to keep track of the number of degrees
    uint256 public degreesCount;

    // Event emitted when a new degree is added
    event DegreeAdded(uint256 degreeId, uint256 matricule, uint256 studentId, string degreeName, uint256 completionYear, uint256 averageObtention, DiplomaMention diplomaMention);

    // Reference to the StudentContract
    StudentContract public studentContract;

    // Constructor to set the address of the StudentContract
    constructor(address _studentContractAddress) public {
        studentContract = StudentContract(_studentContractAddress);
    }

    // Function to add a new degree
    function addDegree(uint256 _matricule, uint256 _studentId, string memory _degreeName, uint256 _completionYear, uint256 _averageObtention, uint8 _diplomaMention) public {
        require(_studentId > 0 && _studentId <= studentContract.studentsCount(), "Invalid student ID");
        require(_averageObtention >= 0 && _averageObtention <= 100, "Invalid average obtention");
        require(_diplomaMention >= 0 && uint8(_diplomaMention) <= uint8(DiplomaMention.Excellent), "Invalid diploma mention");

        degreesCount++;
        degrees[degreesCount] = Degree(degreesCount, _matricule, _studentId, _degreeName, _completionYear, _averageObtention, DiplomaMention(_diplomaMention));
        emit DegreeAdded(degreesCount, _matricule, _studentId, _degreeName, _completionYear, _averageObtention, DiplomaMention(_diplomaMention));
    }

    // Function to get degree details by ID
    function getDegree(uint256 _degreeId) public view returns (uint256, uint256, uint256, string memory, uint256, uint256, DiplomaMention) {
        require(_degreeId > 0 && _degreeId <= degreesCount, "Invalid degree ID");
        Degree memory degree = degrees[_degreeId];
        return (degree.degreeId, degree.matricule, degree.studentId, degree.degreeName, degree.completionYear, degree.averageObtention, degree.diplomaMention);
    }
}
